import { createSlide } from "@reduxjs/toolkit";
import { createAsyscThunk } from "@reduxjs/toolkit";
import groupApi from "../../api/GroupApi";
import { GET_LIST_GROUP } from "../actionTypes";

export const getListGroupAsysncAction = createAsyscThunk(
  GET_LIST_GROUP,
  async () => {
    const groups = await groupApi.getAll();
    return groups;
  }
);

const groupSlide = createSlide({
  name: "group",
  initialState: {
    groups: [],
    isLoading: false,
  },
  reducer: {
    // other reducers
  },
  extraReducer: {
    [getListGroupAsysncAction.fulfilled]: (state, action) => {
      state.groups = action.payload;
      state.isLoading = false;
    },
    [getListGroupAsysncAction.pending]: (state) => {
      state.isLoading = true;
    },
  },
});

export const { actions, reducer } = groupSlide;
export const { incremented, decremented } = actions;

export default groupSlide;
